class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(), greater<int>());
        int p1=0, p2=0, c1=1, c2=1;
        while(p1<h.size() && p2<v.size()){
            // int po1=0, po2=0;
            // if(p1<h.size()) p1=h[p1];
            // if(p2<v.size()) p2=v[p2];
            long long r1=h[p1], r2=v[p2];
            if(r1 >= r2){
                ans += ((long long)r1*c2);
                p1++;
                c1++;
            }
            else{
                ans += ((long long)r2*c1);
                p2++;
                c2++;
            }
        }
        while(p1<h.size()){
            ans += ((long long)h[p1]*c2);
            p1++;
        }
        while(p2<v.size()){
            ans += ((long long)v[p2]*c1);
            p2++;
        }
        return ans;
    }
};
