class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.rbegin(), hor.rend()); 
        sort(ver.rbegin(), ver.rend());
        long long v = 1, h = 1;
        long long i = 0, j = 0, cost = 0;

        while(i < m - 1 && j < n - 1){
            if(hor[i] >= ver[j]){
                cost += (hor[i] * h);
                v++, i++;
            }
            else{
                cost += (ver[j] * v);
                h++, j++;
            }
        }

        while(i < m - 1){
            cost += (hor[i] * h);
            v++, i++;
        }
        while(j < n - 1){
            cost += (ver[j] * v);
            h++, j++;
        }
        return cost;
    }
};
