class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int cx=1,cy=1;
        priority_queue<pair<int,int>>pq;
        for(int i=0;i<m-1;i++){
            pq.push({h[i],0});
        }
        for(int i=0;i<n-1;i++){
            pq.push({v[i],1});
        }
        long long res=0;
        while(!pq.empty()){
            pair<int,int> a=pq.top();
            pq.pop();
            if(a.second){
                res+=a.first*cx;
                cy++;
            }else{
                res+=a.first*cy;
                cx++;
            }
        }
        return res;
    }
};
