using LL = long long ;
class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& a, vector<int>& b) {
        ranges::sort(a, [](auto t1, auto t2){
            return t1 > t2;
        });
        ranges::sort(b, [](auto t1, auto t2){
            return t1 > t2;
        });
        LL ans = 0;
        LL di = 1, dj = 1;
        int i = 0, j = 0;
        n -- ,m --;
        while(i < n && j < m){
            if(a[i] >= b[j]){
                ans += dj * a[i ++ ];
                di ++ ;
            } else {
                ans += di * b[j ++ ];
                dj ++ ;
            }
        }
        while(i < n){
            ans += dj * a[i ++ ];
        }
        while(j < m){
            ans += di * b[j ++ ];
        }
        return ans;
    }
};
