class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end(), greater<int>());
        sort(vc.begin(), vc.end(), greater<int>());
        n--; m--;
        int h=1, v=1;
        int i=0, j=0;
        long long cost=0;
        while (i<n && j<m){
            if (hc[i]>vc[j]){
                cost+=hc[i]*(v);
                h++;
                i++;
            } else {
                cost+=vc[j]*(h);
                v++;
                j++;
            }
        }

        while (i<n){
            cost+=hc[i]*v;
            i++;
        }

        while (j<m){
            cost+=vc[j]*h;
            j++;
        }
        return cost;
    }
};
