class Solution {

public:
     #define ll long long
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
     ll ans=0;
        int i=0,j=0;
           sort(h.begin(),h.end());
           sort(v.begin(),v.end());

        reverse(h.begin(),h.end());
        reverse(v.begin(),v.end());
        int hp=1;
        int vp=1;




        while (i < m-1 && j < n-1) {
            if (h[i] >= v[j]) {
               ans+=(ll)(h[i]*vp);
                 hp++;
                i++;
            } else {
                ans+=(ll)(v[j] * hp);
                vp++;
                j++;
            }
        }

int cnt=0;
        while (i < m-1) {
           ans+=(ll)(h[i]*vp);
                 hp++;
                i++;
        }


        while (j < n-1 ){
             ans+=(ll)(v[j] * hp);
                vp++;
                j++;
        }

        return ans;
    }
};
