class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []

        for i in range(len(horizontalCut)):
            cuts.append((horizontalCut[i], 0))

        for i in range(len(verticalCut)):
            cuts.append((verticalCut[i], 1))

        hdone = 1
        vdone = 1

        cuts.sort(key=lambda x: -x[0])

        ans = 0

        for (cost, t) in cuts:
            if t == 0:
                ans += cost * vdone
                hdone += 1
            else:
                ans += cost * hdone
                vdone += 1

        return ans
