class Solution {
    public long minimumCost(int m, int n, int[] hcut, int[] vcut) {
        Arrays.sort(hcut);
        Arrays.sort(vcut);
        int hi = m - 2, vi = n - 2;
        int vp = 1, hp = 1;
        long ans = 0;
        while (vi > -1 && hi > -1) {
            if (vcut[vi] <= hcut[hi]) {
                ans += (hcut[hi] * vp);
                hi--;
                hp++;
            } else {
                ans += (vcut[vi] * hp);
                vi--;
                vp++;
            }
        }
        while (vi > -1) {
            ans += (vcut[vi] * hp);
            vi--;
            vp++;
        }
        while (hi > -1) {
            ans += (hcut[hi] * vp);
            hi--;
            hp++;
        }
        return ans;
    }
}
