class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<int> p1(h.begin(), h.end());
        priority_queue<int> p2(v.begin(), v.end());
        long long a=1;
        long long b=1;
        long long ans=0;
        while(!p1.empty() || !p2.empty()){
            if(!p1.empty() && (p2.empty() || p1.top()>=p2.top())) {
                ans+=p1.top()*b;
                p1.pop();
                a++;
            } 
            else{
                ans+=p2.top()*a;
                p2.pop();
                b++;
            }
        }
        return ans;
    }
};
