class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        arr = []
        for x in h:
            arr.append([x, 0])
        for x in v:
            arr.append([x, 1])
        arr.sort(key=lambda x: x[0], reverse=True)

        hmultiple = 1
        vmultiple = 1

        result = 0
        for x in arr:
            if (x[1] == 0):
                result += (x[0] * hmultiple)
                vmultiple += 1
            else:
                result += (x[0] * vmultiple)
                hmultiple += 1
        return result
