class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<long long,char>> vp;
        for(auto i:h)
        {
            vp.push_back({i,'h'});
        }
        for(auto i:v){
            vp.push_back({i,'v'});
        }
        long long cv,ch;
        cv=1;
        ch=1;
        long long c=0;
        sort(vp.begin(),vp.end());
        for(int i=vp.size()-1;i>=0;i--){
            if(vp[i].second=='h'){
                c+=vp[i].first*cv;
                ch++;

            }
            else{
                c+=vp[i].first*ch;
                cv++;
            }

        }
        return c;


    }
};
