class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        long long hcnt = 1, vcnt = 1;
        int i = 0, j = 0;
        long long ans = 0;
        while(i<hc.size() && j<vc.size()){
            if(hc[i] >= vc[j]){
                ans += (hc[i])*vcnt;
                i++;
                hcnt++;
            }
            else{
                ans += (vc[j])*hcnt;
                j++;
                vcnt++;
            }
        }

        while(i<hc.size()){
            ans += (hc[i])*vcnt;
            i++;
        }

        while(j<vc.size()){
            ans += (vc[j])*hcnt;
            j++;
        }

        return ans;
    }
};
