class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0;
        int l = 1,r = 1;
        vector<pair<int,int>>a;
        for(int p : horizontalCut){
            a.push_back({p,0});
        }
        for(int p : verticalCut){
            a.push_back({p,1});
        }
        sort(a.begin(),a.end(),greater<pair<int,int>>());
        for(auto [c,p] : a){
            if(p == 0){
                ans += 1ll * c * r;
            }else{
                ans += 1ll * c * l;
            }
            if(p == 0)l++;
            else r++;
        }
        return ans;
    }
};
