class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        long long H = 1, V = 1, ans = 0;
        sort(hcut.begin(), hcut.end());
        sort(vcut.begin(), vcut.end());
        int i = hcut.size() - 1, j = vcut.size() - 1;

        while (i >= 0 && j >= 0) {
            if (hcut[i] > vcut[j]) {
                ans += hcut[i] * V;
                H++;
                i--;
            } else {
                ans += vcut[j] * H;
                V++;
                j--;
            }
        }

        while (i >= 0) {
            ans += hcut[i] * V;
            H++;
            i--;
        }

        while (j >= 0) {
            ans += vcut[j] * H;
            V++;
            j--;
        }

        return ans;
    }
};
