class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>> cost;
        for(auto i:h){cost.push_back({i,0});}
        for(auto i:v){cost.push_back({i,1});}
        sort(cost.begin(),cost.end(),[](auto x,auto y){
            return x.first>y.first;
        });
        long long ch=0,cv=0;
        long long ans=0;
        for(auto i:cost){
            int x=i.first,y=i.second;
            if(y){
                ans+=(ch+1)*x;
                cv++;
            }else{
                ans+=(cv+1)*x;
                ch++;
            }
        }
        return ans;
    }
};
