from heapq import heappop , heappush
class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        heap=[]
        for i in range(m-1):
            heappush(heap, [-horizontalCut[i],i,0])
        for i in range(n-1):
            heappush(heap, [-verticalCut[i],i,1])
        ans=0 
        h=1 
        v=1
        while len(heap):
            a,b,c= heappop(heap)
            if c==0:
                ans+= (-a*v)
                h+=1 
            else:
                ans+= (-a*h)
                v+=1 
        return ans
