class Solution {
public:
    using ll = long long;
    const ll inf = 1e18;
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<ll> used(2);
        priority_queue<pair<ll, ll>> pq;
        for (auto& h : hc) {
            pq.emplace(h, 0);
        }
        for (auto& v : vc) {
            pq.emplace(v, 1);
        }

        ll ans = 0;
        while (pq.size()) {
            auto [c, op] = pq.top();
            pq.pop();
            if (op == 0) {
                ll x = used[1] + 1;
                ans += c * x;
                used[0]++;
            } else {
                ll x = used[0] + 1;
                ans += c * x;
                used[1]++;
            }

        }

        return ans;
    }
};
