function minimumCost(m: number, n: number, horizontalCut: number[], verticalCut: number[]): number {
  let col = 1,
    row = 1
  horizontalCut.sort((a, b) => b - a)
  verticalCut.sort((a, b) => b - a)
  let res = 0
  for (let i = 0, j = 0; i < horizontalCut.length || j < verticalCut.length; ) {
    if (i < horizontalCut.length && horizontalCut[i] > (verticalCut[j] ?? -Infinity)) {
      row++
      res += col * horizontalCut[i++]
    } else {
      col++
      res += row * verticalCut[j++]
    }
  }
  return res
}
