class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        int x = 0 , y = 0;
        vector<pair<int,int>> vec;
        for(auto &k:h)
        {
            vec.push_back({k,1});
        }
        for(auto &k:v)
        {
            vec.push_back({k,2});
        }
        sort(vec.begin() , vec.end());
        reverse(vec.begin() , vec.end());
        for(auto &k:vec)
        {
            if(k.second == 1)
            {
                ans += (long long)(k.first) * (long long)(y + 1);
                x++;
            }
            else
            {
                ans += (long long)(k.first) * (long long)(x + 1);
                y++;
            }
        }
        return ans;
    }
};
