class Solution {
using ll = long long;
public:
    long long minimumCost(int m, int n, vector<int>& hr, vector<int>& vr) {
        ll ans = 0;
         sort(hr.rbegin(), hr.rend());
        sort(vr.rbegin(), vr.rend());
        int h = 1, vlen = 1,i = 0, j = 0;
        for (; i < (int)hr.size() && j < (int)vr.size();) {
            if (hr[i] >= vr[j]) {
                ans += ((ll)(hr[i] * vlen));
                h++;
                i++;
            } else {
                ans += (ll)(vr[j] * h);
                vlen++;
                j++;
            }
        }
         while (i < (int)hr.size()) {
            ans += (ll)hr[i] * vlen;
            i++;
        }
        while (j < (int)vr.size()) {
            ans += (ll)vr[j] * h;
            j++;
        }
        return ans;
    }
};
