class Solution {
    public long minimumCost(int m, int n, int[] x, int[] y) {
        long ans=0;
        Arrays.sort(x);
        Arrays.sort(y);
        int a=x.length-1;
        int b=y.length-1;

        int ver=1;
        int hor=1;

        while(true){
            int p=0;
            int q=0;
            if(a>=0)
                p=x[a];
            if(b>=0)    
                q=y[b];

            if(p>=q){
                ans+=(long)((long)p*(long)hor);
                ver++;
                a--;
            }else{
                ans+=(long)((long)q*(long)ver);
                hor++;
                b--;
            }
            if(a<0 && b<0){
                break;
            }
        }
        return ans;
    }
}
