class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(vc.rbegin(), vc.rend());
        sort(hc.rbegin(), hc.rend());
        long long h = 1, v = 1;  
        long long i = 0, j = 0;
        long long ans = 0;
        while (i < hc.size() && j < vc.size()) {
            if (hc[i] >= vc[j]) {
                ans += hc[i] * v;
                i++;
                h++;
            } else {
                ans += vc[j] * h;
                j++;
                v++;
            }
        }

        while (i < hc.size()) {
            ans += hc[i] * v;
            i++;
        }

        while (j < vc.size()) {
            ans += vc[j] * h;
            j++;
        }
        return ans;
    }
};
