class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<int> h(hc.begin(), hc.end());
        priority_queue<int> v(vc.begin(), vc.end());

        h.push(0);
        v.push(0);

        long long hpiece = 1, vpiece = 1;
        long long ans = 0;

        while (!h.empty() && !v.empty()) {
            if (h.top() >= v.top()) {
                long long cost = h.top();
                h.pop();
                ans += cost * vpiece;
                hpiece++;
            } else {
                long long cost = v.top();
                v.pop();
                ans += cost * hpiece;
                vpiece++;
            }
        }

        return ans;
    }
};
