class Solution {
    struct Cut {
        bool h; // horizontal
        long long value;
    };

    static bool cmp(const Cut& a, const Cut&b) {
        return a.value > b.value;
    }

public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<Cut> v;
        for(int i = 0; i < horizontalCut.size(); i++) {
            v.push_back({true, horizontalCut[i]});
        }
        for(int j = 0; j < verticalCut.size(); j++) {
            v.push_back({false, verticalCut[j]});
        }
        sort(v.begin(), v.end(), cmp);
        long long ans = 0;
        long long verticals = 0;
        long long horizontals = 0;
        for(int i = 0; i < v.size(); i++) {
            if(v[i].h) {
                ans += v[i].value*(verticals+1);
                horizontals++;
            } else {
                ans += v[i].value*(horizontals+1);
                verticals++;
            }
        }
        return ans;
    }
};
