class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
    priority_queue<pair<int,int>>pq;
    for(auto it : h){
        pq.push({it,0});
    }
    for(auto it : v){
        pq.push({it,1});
    }
    int vcount=0,hcount=0;
    long long ans=0;
    while(!pq.empty()){
        int type = pq.top().second;
        int cost = pq.top().first;
        pq.pop();
        if(type==0){
            ans+=(cost*(vcount+1));
            hcount++;
        }
        else {
            ans+=(cost*(hcount+1));
            vcount++;
        }
    }
    return ans;
    }
};
