#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<ll,ll>> pq;
        ll hmult = 1LL, vmult = 1LL;
        ll tot = 0LL;
        for(auto& x: h) pq.push({x, 0});
        for(auto& x: v) pq.push({x, 1});

        while (pq.size()) {
            auto [cost, dir] = pq.top(); pq.pop();
            if (dir == 0) {
                tot += hmult * cost;
                vmult++;
            } else {
                tot += vmult * cost;
                hmult++;
            }
        }

        return tot;


    }
};
