class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,bool>> maintain;
        for(auto it:horizontalCut){
            maintain.push_back({0-it,true});
        }

        for(auto it:verticalCut){
            maintain.push_back({0-it,false});
        }
        sort(maintain.begin(),maintain.end());
        long long ans = 0;
        int hp = 1, vp = 1 ;
        for(auto it:maintain){
            if(it.second){
                ans+=abs(it.first-0)*vp;
                hp++;
            }else{
                ans+=abs(it.first-0)*hp;
                vp++;

            }
        }
        return ans;
    }
};
