class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {

        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        int hIndex = 0, vIndex = 0;
        long long tc = 0; 

        int hp = 1, vp = 1; 



        while (hIndex < hc.size() && vIndex < vc.size()) {

            if (hc[hIndex] > vc[vIndex]) {

                tc += static_cast<long long>(hc[hIndex]) * vp;
                hIndex++;
                hp++;
            } else {
                tc += static_cast<long long>(vc[vIndex]) * hp;
                vIndex++;
                vp++;
            }
        }


        while (hIndex < hc.size()) {

            tc += static_cast<long long>(hc[hIndex]) * vp;
            hIndex++;
            hp++;
        }


        while (vIndex < vc.size()) {

            tc += static_cast<long long>(vc[vIndex]) * hp;
            vIndex++;
            vp++;
        }

        return tc;
    }
};
