class Solution:
    def minimumCost(self, m: int, n: int, hs: List[int], vs: List[int]) -> int:
        hs.sort(reverse=True)
        vs.sort(reverse=True)

        i = j = 0

        res = 0
        while i < len(hs) or j < len(vs):
            if i==len(hs):
                res += vs[j] * (i+1)
                j += 1
            elif j == len(vs):
                res += hs[i] * (j+1)
                i += 1
            else:
                if hs[i] > vs[j]:
                    res += hs[i] * (j+1)
                    i += 1
                else:
                    res += vs[j] * (i+1)
                    j += 1
        return res
