class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(all(hc)); sort(all(vc));
        ll tot = 0, hcut = 0, vcut = 0;
        while (siz(hc) + siz(vc)){
            if (hc.empty()){
                tot += vc.back()*(hcut+1);
                vc.pop_back();
                vcut++;
            } else if (vc.empty()){
                tot += hc.back()*(vcut+1);
                hc.pop_back();
                hcut++;
            } else if (hc.back() > vc.back()){
                tot += hc.back()*(vcut+1);
                hc.pop_back();
                hcut++;
            } else {
                tot += vc.back()*(hcut+1);
                vc.pop_back();
                vcut++;
            }
        }
        return tot;
    }
};
