class Solution {
    public long minimumCost(int m, int n, int[] H, int[] V) {
        int v = 1;
        int h = 1;
        ArrayList<Integer> list1 = new ArrayList<>();
        ArrayList<Integer> list2 = new ArrayList<>();
        for(int i=0; i<m-1; i++){
            list1.add(i);
        }
        for(int i=0; i<n-1; i++){
            list2.add(i);
        }
        Collections.sort(list1, Comparator.comparingInt(o->-H[o]));
        Collections.sort(list2, Comparator.comparingInt(o->-V[o]));
        int i = 0,j=0;
        long ans = 0;
        while(i < m-1 && j < n-1){
            int I = list1.get(i); int J = list2.get(j);
            if(H[I] > V[J]){
                ans += v * H[I];
                h++; i++;
                // System.out.println(v * H[list1.get(i-1)]);
            }else{
                ans += h * V[J];
                v++; j++;
                // System.out.println(h * V[list2.get(j-1)]);
            }
        }
        while(i < m-1){
            int I = list1.get(i); 
            ans += v * H[I];
            h++; i++;
            // System.out.println(v * H[list1.get(i-1)]);
        }
        while(j < n-1){
            int J = list2.get(j);
            ans += h * V[J];
            v++; j++;
            // System.out.println(h * V[list2.get(j-1)]);
        }
        return ans;
    }
}
