class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<array<int,3>>comb;
        for(auto i:horizontalCut){
            comb.push_back({i,1,0});
        }
        for(auto i:verticalCut){
            comb.push_back({i,0,1});
        }
        sort(comb.rbegin(),comb.rend());
        int vert=0,hort=0;
        long long ans=0;
        for(auto i:comb){
            if(i[1]==1){
                hort+=1;
                ans+=1LL*(vert+1)*i[0];
            }else{
                vert+=1;
                ans+=1LL*(hort+1)*i[0];
            }
        }
        return ans;
    }
};
