class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        std::sort(horizontalCut.begin(), horizontalCut.end(), std::greater<int>());
        std::sort(verticalCut.begin(), verticalCut.end(), std::greater<int>());

        long long totalCost = 0;
        int h = 1, v = 1; // Initial number of horizontal and vertical segments
        int i = 0, j = 0;

        // Process the cuts in descending order
        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] >= verticalCut[j]) {
                totalCost += static_cast<long long>(horizontalCut[i]) * v;
                h++;
                i++;
            } else {
                totalCost += static_cast<long long>(verticalCut[j]) * h;
                v++;
                j++;
            }
        }

        // Process remaining horizontal cuts
        while (i < horizontalCut.size()) {
            totalCost += static_cast<long long>(horizontalCut[i]) * v;
            i++;
        }

        // Process remaining vertical cuts
        while (j < verticalCut.size()) {
            totalCost += static_cast<long long>(verticalCut[j]) * h;
            j++;
        }

        return totalCost;
    }
};
