class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,bool>> pq;
        for(auto i:horizontalCut){
            pq.push({i,false});
        }
        for(auto i:verticalCut){
            pq.push({i,true});
        }
        int v=1,h=1;
        long long ans=0;
        while(!pq.empty()){
            int toCut=pq.top().first;
            bool vert=pq.top().second;
            pq.pop();
            if(vert){
                h++;
                ans+=toCut*v;
            }else{
                v++;
                ans+=toCut*h;
            }
        }
        return ans;
    }
};
