class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {

        long long nh=1,nv=1;
        sort(hc.begin(),hc.end(),greater<>());
         sort(vc.begin(),vc.end(),greater<>());

        long long hs=hc.size(),vs=vc.size(),i=0,j=0;
        long long cost=0;

        while(i<hs&&j<vs){

            if(hc[i]>vc[j]){
                cost+=1LL*hc[i]*nv;
                ++nh;
                ++i;
            }else{
                cost+=1LL*vc[j]*nh;
                ++j;
                ++nv;
            }

        }

        while(i<hs){
             cost+=1LL*hc[i]*nv;
                ++nh;
                ++i;
        }
        while(j<vs){
             cost+=1LL*vc[j]*nh;
                ++j;
                ++nv;
        }

        return cost;

    }
};
