class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        //maxheap
        priority_queue<pair<int,bool>>pq;
        for(auto x:h){
            pq.push({x,true});
        }
        for(auto x:v){
            pq.push({x,false});
        }
        long long ans=0LL,hp=1,vp=1;
        while(!pq.empty()){
            auto c=pq.top().first;
            auto flag=pq.top().second;
            pq.pop();
            if(flag){
                ans+=(c*vp);
                hp++;
            }
            else{
                ans+=(c*hp);
                vp++;
            }
        }
        return ans;
    }
};
