class Solution {
public:
    struct Node{
        int v;
        int op; //0, 1
        bool operator<(const Node& x) const{
            return v<x.v;
        }
    };
    long long minimumCost(int n, int m, vector<int>& hc, vector<int>& vc) {
        long long res=0, nc=1, mc=1;
        priority_queue<Node> q;
        for(auto &v:hc) q.push({v, 0});
        for(auto &v:vc) q.push({v, 1});

        while(!q.empty()){
            auto t=q.top();
            q.pop();

            if(t.op==0){
                res+=nc*t.v;
                ++mc;
            }else{
                res+=mc*t.v;
                ++nc;
            }
        }
        return res;
    }
};
