typedef long long ll;
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(),hc.rend());
        sort(vc.rbegin(),vc.rend());
        ll hidx=0,vidx=0,h=1,v=1;
        ll tc=0;
        while(hidx<m-1 && vidx<n-1)
        {
            if(vc[vidx]>hc[hidx])
            {
                tc=tc+h*vc[vidx];
                v++;
                vidx++;
            }
            else
            {
                tc=tc+v*hc[hidx];
                h++;
                hidx++;
            }
        }
        while(hidx<m-1)
        {
            tc=tc+v*hc[hidx];
                h++;
                hidx++;
        }
        while(vidx<n-1)
        {
            tc=tc+h*vc[vidx];
                v++;
                vidx++;
        }
        return tc;
    }
};
