class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hheap = [-i for i in horizontalCut]
        vheap = [-i for i in verticalCut]
        heapify(hheap)
        heapify(vheap)
        m1 = 1
        m2 = 1
        s = 0
        for i in range(m+n-2):
            if not hheap:
                c = 1
            elif not vheap:
                c = 0
            else:
                if hheap[0]<=vheap[0]:
                    c = 0
                else:
                    c = 1
            if c==0:
                cur = heappop(hheap)
                m2+=1
                s+=m1*cur
            else:
                cur = heappop(vheap)
                m1+=1
                s+=m2*cur
        return -s
