class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
                sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        long long horizontalSegments = 1;
       long long verticalSegments = 1;
        long long totalCost = 0;

        int i = 0, j = 0;

        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] > verticalCut[j]) {
                totalCost += horizontalCut[i] * verticalSegments;
                horizontalSegments++;
                i++;
            } else {
                totalCost += verticalCut[j] * horizontalSegments;
                verticalSegments++;
                j++;
            }
        }

        while (i < horizontalCut.size()) {
            totalCost += horizontalCut[i] * verticalSegments;
            i++;
        }

        while (j < verticalCut.size()) {
            totalCost += verticalCut[j] * horizontalSegments;
            j++;
        }

        return totalCost;
    }
};
