class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());
        long long i = horizontalCut.size()-1;
        long long j = verticalCut.size()-1;
        long long ans = 0;
        long long v = 1, h = 1;
        while(i>=0 && j>=0){
            if(horizontalCut[i]>verticalCut[j])  ans+=horizontalCut[i--] * v, h++;
            else           ans+=verticalCut[j--] * h, v++;
        }
        while(i>=0) ans+=horizontalCut[i--] * v;
        while(j>=0) ans+=verticalCut[j--] * h;
        return ans;
    }
};
