class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());
        long long h_part = 1;
        long long v_part = 1;
        int i = m-2, j=n-2;
        long long ans = 0;
        while(i >= 0 && j >= 0){
            if(hc[i] >= vc[j]){
                ans += hc[i]*v_part;
                h_part++;
                i--;
            }
            else{
                ans += vc[j]*h_part;
                v_part++;
                j--;
            }
        }
        while(i >= 0){
            ans += hc[i]*v_part;
            i--;
        }
        while(j >= 0){
            ans += vc[j]*h_part;
            j--;
        }
        return ans;
    }
};
