class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        long res = 0, xCost = 1, yCost = 1;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int x = horizontalCut.length - 1, y = verticalCut.length - 1;
        while(x >= 0 || y >= 0) {
            if (x < 0) {
                while (y >= 0) {
                    res += (long)(verticalCut[y] * yCost);
                    y--;
                }
                break;
            }

            if (y < 0) {
                while (x >= 0) {
                    res += (long)(horizontalCut[x] * xCost);
                    x--;
                }
                break;
            }
            int xVal = horizontalCut[x], yVal = verticalCut[y];
            if (x >= 0 && horizontalCut[x] > verticalCut[y]) {
                res += (long)(horizontalCut[x] * xCost);
                yCost++;
                x--;
            } else {
                res += (long)(yVal * yCost);
                xCost++;
                y--;
            }
        }
        return res;
    }
}
