class Solution {
public:
    typedef long long ll;
    struct Node {
        ll val;
        int tp;
        bool operator<(const Node& other) const {
            return val < other.val;
        };
    };
    long long minimumCost(int n, int m, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<Node> pq;
        for(int i = 0; i < n - 1; i++) pq.push({horizontalCut[i], 0});
        for(int j = 0; j < m - 1; j++) pq.push({verticalCut[j], 1});
        ll c0 = 0, c1 = 0;
        ll ans = 0;
        while(pq.size()) {
            auto [val, tp] = pq.top(); pq.pop();
            if(tp == 0) ans += val * (c1 + 1), c0++;
            else ans += val * (c0 + 1), c1++;
        }
        return ans;
    }
};
