class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        auto h = horizontalCut;
        auto v = verticalCut;
        vector<pair<int, int>> all;
        for(int x : h){
            all.push_back({x, 0});
        }
        for(int x : v){
            all.push_back({x, 1});
        }
        sort(all.begin(), all.end());
        long long ans = 0;
        long long vv = 1, hh = 1;
        for(int i = m + n - 3; i >= 0; i--){
            if(all[i].second == 0){
                ans += (all[i].first * hh);
                vv++;
            } else {
                ans += (all[i].first * vv);
                hh++;
            }
        }
        return ans;
    }
};
