class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        pq=[]
        for x in horizontalCut:
            pq.append((x,0))
        for x in verticalCut:
            pq.append((x,1))
        pq.sort(reverse=True)
        count=[0,0]
        res=0
        for x,i in pq:
            res+=x*(count[1-i]+1)
            count[i]+=1
        return res
