class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        long res = 0l;
        int i = m - 2, j = n - 2, p = 0, x = 0, y = 0;
        while (i >= 0 || j >= 0) {
            if (i >= 0 && (j < 0 || h[i] > v[j] || (h[i] == v[j] && p == 1))) {
                if (p != 1) p = 1;
                res += (long)((y + 1) * h[i]);
                i--;
                x++;
            } else {
                if (p != -1) p = -1;
                res += (long)((x + 1) * v[j]);
                j--;
                y++;
            }
            // System.out.println(res);
        }
        return res;
    }
}
