#define ll long long
class Solution {
public:
    static bool cmp(pair<int, char>&a, pair<int, char>&b) {return a.first > b.first;}
    ll minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>>vp;
        for(int h : horizontalCut) vp.push_back({h, 'H'});
        for(int v : verticalCut) vp.push_back({v, 'V'});
        sort(vp.begin(), vp.end(), cmp);
        ll hor = 0, ver = 0, ans = 0;
        for(auto it : vp){
            int cost = it.first;
            char dir = it.second;
            if(dir == 'H'){
                ans += (ver + 1) * cost;
                hor ++;
            }
            else{
                ans += (hor + 1) * cost;
                ver ++;
            }
        }
        return ans;
    }
};
