class Solution {
public:
    long long minimumCost(int width, int height, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
        priority_queue<int> hCuts(horizontalCuts.begin(), horizontalCuts.end());
        priority_queue<int> vCuts(verticalCuts.begin(), verticalCuts.end());

        map<string, function<void()>> actions;

        int hPieces = 1, vPieces = 1;
        long long totalCost = 0;
        actions["horizontal"] = [&]() {
            totalCost += static_cast<long long>(hCuts.top()) * vPieces;
            hPieces++;
            hCuts.pop();
        };

        actions["vertical"] = [&]() {
            totalCost += static_cast<long long>(vCuts.top()) * hPieces;
            vPieces++;
            vCuts.pop();
        };

        while (!hCuts.empty() && !vCuts.empty()) {
            if (hCuts.top() > vCuts.top()) {
                actions["horizontal"]();
            } else {
                actions["vertical"]();
            }
        }
        while (!hCuts.empty()) {
            actions["horizontal"]();
        }
        while (!vCuts.empty()) {
            actions["vertical"]();
        }

        return totalCost;
    }
};
