class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {


        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
       long long int cost = 0;
        int i = m-2 , j = n-2 ,hz =1 ,vt = 1; 
        while(i >=0 && j >=0){
            if(h[i]>=v[j]){
                cost+= 1ll*hz*h[i];
                i--;
                vt++;
            }
            else{
                cost+= 1ll*vt*v[j];
                j--;
                hz++;
            }
        }
        while(i>=0){
             cost+= 1ll*hz*h[i];
                i--;
                vt++;
        }
        while(j>=0){
             cost+= 1ll*vt*v[j];
                j--;
                hz++;
        }
        return cost;
    }

};
